import React from 'react';

export default function PhilosophySection() {
  return (
    <div style={{ backgroundColor: 'rgb(235 220 213)', padding: '60px 20px', textAlign: 'center' }}>
      <h2 style={{ fontSize: '2rem', marginBottom: '16px', color: '#c97e93' }}>
        Our Philosophy
      </h2>
      <p style={{ maxWidth: '700px', margin: '0 auto', fontSize: '1.1rem', color: '#555' }}>
        At Serene Essence, we believe in more than fragrance. We believe in moments of stillness, in the slow rituals that center you. Every candle is hand-poured in small batches, with sustainable soy wax and essential oils sourced for their purity and soul-soothing qualities.  
        <br /><br />
        We're here to help you pause, breathe, and find a little luxury in your every day.
      </p>
    </div>
  );
}
